import { Component, OnInit, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { MiddlewareService, BackendService } from '../../core/services/middleware.service';

@Component({
  selector: 'app-backend-management',
  standalone: true,
  imports: [CommonModule, FormsModule],
  template: `
    <div class="container">
      <h2>Gestión de Microbackends</h2>
      
      <!-- Formulario Alta -->
      <div class="card shadow p-4 mb-4">
        <h4>Registrar Nuevo Servicio</h4>
        <div class="row g-3">
          <div class="col-md-3">
            <input type="text" [(ngModel)]="newService.id" class="form-control" placeholder="ID (ej: pais-svc)">
          </div>
          <div class="col-md-3">
            <input type="text" [(ngModel)]="newService.nombre" class="form-control" placeholder="Nombre Comercial">
          </div>
          <div class="col-md-2">
            <input type="number" [(ngModel)]="newService.puerto" class="form-control" placeholder="Puerto (8000)">
          </div>
          <div class="col-md-2">
            <button (click)="register()" class="btn btn-primary w-100">Registrar</button>
          </div>
        </div>
      </div>

      <!-- Listado y Análisis -->
      <div class="row">
        <div class="col-md-6">
          <div class="card shadow">
            <div class="card-header bg-dark text-white">Servicios Registrados</div>
            <ul class="list-group list-group-flush">
              <li *ngFor="let svc of services" class="list-group-item d-flex justify-content-between align-items-center">
                <div>
                  <strong>{{ svc.nombre }}</strong> <br>
                  <small class="text-muted">{{ svc.host }}:{{ svc.puerto }}</small>
                </div>
                <button (click)="inspect(svc.id)" class="btn btn-sm btn-outline-info">Analizar Contrato</button>
              </li>
            </ul>
          </div>
        </div>

        <!-- Inspector de Endpoints -->
        <div class="col-md-6" *ngIf="selectedInspection">
          <div class="card shadow border-info">
            <div class="card-header bg-info text-white">
              Endpoints de: {{ selectedInspection.service_name }}
            </div>
            <div class="card-body scrollable-area">
              <div *ngFor="let ep of selectedInspection.endpoints" class="endpoint-item mb-2 p-2 border-bottom">
                <span class="badge" [ngClass]="getMethodClass(ep.method)">{{ ep.method }}</span>
                <code class="ms-2">{{ ep.path }}</code>
                <div class="text-muted small mt-1">{{ ep.summary }}</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>

    <style>
      .container { padding-top: 2rem; }
      .endpoint-item { font-size: 0.9rem; }
      .scrollable-area { max-height: 500px; overflow-y: auto; }
      .badge-GET { background-color: #61affe; }
      .badge-POST { background-color: #49cc90; }
      .badge-PUT { background-color: #fca130; }
      .badge-DELETE { background-color: #f93e3e; }
      .badge-PATCH { background-color: #50e3c2; }
    </style>
  `
})
export class BackendManagementComponent implements OnInit {
  private service = inject(MiddlewareService);
  
  services: BackendService[] = [];
  newService: Partial<BackendService> = { host: 'http://127.0.0.1' };
  selectedInspection: any = null;

  ngOnInit() {
    this.loadServices();
  }

  loadServices() {
    this.service.getBackendServices().subscribe(data => this.services = data);
  }

  register() {
    if (!this.newService.id || !this.newService.nombre || !this.newService.puerto) {
      alert('Por favor, completa todos los campos obligatorios (ID, Nombre y Puerto)');
      return;
    }

    this.service.registerBackend(this.newService).subscribe({
      next: () => {
        this.loadServices();
        this.newService = { host: 'http://127.0.0.1', is_active: true };
        alert('Servicio registrado con éxito');
      },
      error: (err) => {
        console.error('Error al registrar:', err);
        alert('Error al registrar el servicio: ' + (err.error?.detail?.[0]?.msg || err.message));
      }
    });
  }

  inspect(id: string) {
    this.service.inspectService(id).subscribe({
      next: (data) => {
        this.selectedInspection = data;
      },
      error: (err) => {
        console.error('Error al inspeccionar:', err);
        alert('No se pudo analizar el microservicio. Asegúrate de que esté encendido en el puerto correcto.');
      }
    });
  }

  getMethodClass(method: string): string {
    return `badge-${method}`;
  }
}
